mapper.select:
select
,
from car_good_info where id = #{id,jdbcType=INTEGER}

SELECT * FROM ( SELECT cg.id, CONCAT( cb.`name`, cf.`name`, cv.`name` ) AS carName, cg.decorations_out_name, cg.decorations_in_name, cg.produce_date, cg.storage_status, IFNULL(cg.sell_date,'-') as sell_date, CONCAT(cw.`name`, '/', cs.`name`) AS warehouse_name, cg.optional_package_status, cg.new_boutique_status, cg.hot_status, cg.vin, cg.interest_price, cg.vehicle_name as vehicleName, cg.year, cg.in_storage_day as inStorageDay FROM car_good_info cg LEFT JOIN car_brand cb ON cb.id = cg.brand_id LEFT JOIN car_family cf ON cg.family_id = cf.id LEFT JOIN car_vehicle_conf cv ON cv.id = cg.vehicle_id LEFT JOIN car_storage cs ON cg.storage_id = cs.id LEFT JOIN car_warehouse cw ON cg.warehouse_id = cw.id WHERE cg.status=1
AND cg.storage_status NOT IN (4,6)
AND cg.distributor_store_id =#{storeId}
AND cg.in_storage_day > #{inStorageDay}
AND cg.family_id =#{familyId}
AND cg.vehicle_id =#{vehicleId}
AND cg.decorations_out_id=#{decorationsOutId}
AND cg.storage_status=#{storageStatus}
)a WHERE 1=1
AND a.carName LIKE CONCAT('%',#{name},'%')

SELECT cg.id, cg.vin, CONCAT( cg.brand_name, cg.family_name, cg.vehicle_name ) AS carName, cg.year, cg.in_storage_day as inStorageDay FROM car_good_info cg WHERE cg.status=1
AND cg.distributor_store_id =#{storeId}
AND cg.in_storage_day > #{inStorageDay}
and cg.storage_status in (2,3,5)

SELECT cgi.id, cgi.car_order_id as carOrderId, cgi.distributor_store_id as distributorStoreId, cgi.vin, cgi.brand_id as brandId, cgi.family_id as family, cgi.vehicle_id as vehicleId, cgi.fund_property as fundProperty, cgi.engine_name as engineName, cgi.decorations_out_id as decorationsOutId, cgi.decorations_out_name as decorationsOutName, cgi.decorations_in_id as decorationsInId, cgi.decorations_in_name as decorationsInName, cgi.engine_num as engineNum, cgi.key_num as keyNum, cgi.certification_num as certificationNum, cgi.import_num as importNum, cgi.warranty_num as warrantyNum, cgi.inspection_num as inspectionNum, cgi.standard_price as standardPrice, cgi.purchase_price as purchasePrice, cgi.purchase_price_pio as purchasePricePio, cgi.freight_charge as freightCharge, cgi.entire_purchase_price as entirePurchasePrice, cgi.include_boutique_price as includeBoutiquePrice, DATE_FORMAT(cgi.purchase_date,'%Y-%m-%d %H:%i:%s')purchaseDate, DATE_FORMAT(cgi.produce_date,'%Y-%m-%d')produceDate, DATE_FORMAT(cgi.offline_date,'%Y-%m-%d %H:%i:%s')offlineDate, cgi.interest_free_day as interestFreeDay, cgi.optional_package_status as optionalPackageStatus, cgi.guaranty_status as guarantyStatus, cgi.finance_company_id as financeCompanyId, cgi.storage_status as storageStatus, cgi.dispose_status as disposeStatus, cgi.order_status as orderStatus, DATE_FORMAT(cgi.in_storage_date,'%Y-%m-%d %H:%i:%s')inStorageDate, DATE_FORMAT(cgi.out_storage_date,'%Y-%m-%d %H:%i:%s')outStorageDate, cgi.warehouse_id as warehouseId, cgi.storage_id as storageId, cgi.car_supply_status as carSupplyStatus, cgi.factory_report_status as factoryReportStatus, cgi.virtual_record_status as virtualRecordStatus, cgi.deceive_report_status as deceiveReportStatus, cgi.new_boutique_status as newBoutiqueStatus, DATE_FORMAT(cgi.sell_date,'%Y-%m-%d %H:%i:%s')sellDate, cgi.forecast_profit as forecastProfit, cgi.count_profit as countProfit, cgi.hot_status as hotStatus, cgi.forward_car_status as forwardCarStatus, DATE_FORMAT(cgi.first_dms_date,'%Y-%m-%d %H:%i:%s')firstDmsDate, DATE_FORMAT(cgi.real_dms_date,'%Y-%m-%d %H:%i:%s')realDmsDate, cgi.operator_id as operatorId, DATE_FORMAT(cgi.create_date,'%Y-%m-%d %H:%i:%s')createDate, DATE_FORMAT(cgi.update_date,'%Y-%m-%d %H:%i:%s')updateDate, cgi.status, cgi.two_dimensional_code as twoDimensionalCode, cgi.purchase_day as purchaseDay, cgi.in_storage_day as inStorageDay, cgi.source, cgi.batch_rebate as batchRebate, cgi.retail_rebate as retailRebate, cb.name as brandName, cb.factory_name as factoryName, cf.name as familyName, cf.source_id as sourceId, cvc.year, cvc.name as vehicleName, fco.name as financeCompanyName, cw.name as warehouseName, cs.name as storageName, cw.picture as warehousePicture FROM car_good_info cgi LEFT JOIN car_brand cb on cgi.brand_id = cb.id LEFT JOIN car_family cf on cgi.family_id = cf.id LEFT JOIN car_vehicle_conf cvc on cgi.vehicle_id = cvc.id LEFT JOIN finance_company fco on cgi.finance_company_id = fco.id LEFT JOIN car_warehouse cw on cgi.warehouse_id = cw.id LEFT JOIN car_storage cs on cgi.storage_id = cs.id
and cgi.vin LIKE CONCAT('%', #{vin,jdbcType=VARCHAR} ,'%')
and cf.source_id = #{sourceId,jdbcType=INTEGER}
and cgi.family_id = #{familyId,jdbcType=INTEGER}
and cgi.vehicle_id = #{vehicleId,jdbcType=INTEGER}
and cgi.storage_status = #{storageStatus,jdbcType=INTEGER}
and cgi.dispose_status = #{disposeStatus,jdbcType=INTEGER}
and cgi.order_status = #{orderStatus,jdbcType=INTEGER}
and cgi.warehouse_id = #{warehouseId,jdbcType=INTEGER}
and cgi.storage_id = #{storageId,jdbcType=INTEGER}
and cgi.car_supply_status = #{carSupplyStatus,jdbcType=INTEGER}
and cgi.factory_report_status = #{factoryReportStatus,jdbcType=INTEGER}
and cgi.virtual_record_status = #{virtualRecordStatus,jdbcType=INTEGER}
and cgi.deceive_report_status = #{deceiveReportStatus,jdbcType=INTEGER}
and cgi.forward_car_status = #{forwardCarStatus,jdbcType=INTEGER}
and cgi.source = #{source,jdbcType=INTEGER}
and cgi.distributor_store_id = #{distributorStoreId,jdbcType=INTEGER} and cgi.status = 1
order by cgi.create_date desc

SELECT cgi.id, cgi.vin, cgi.decorations_out_name as decorationsOutName, cgi.decorations_in_name as decorationsInName, cgi.engine_num as engineNum, cgi.storage_status as storageStatus, cgi.new_boutique_status as newBoutiqueStatus, DATE_FORMAT(cgi.produce_date,'%Y-%m-%d %H:%i:%s') produceDate, DATE_FORMAT(cgi.in_storage_date,'%Y-%m-%d %H:%i:%s') inStorageDate, cgi.in_storage_day as inStorageDay, cgi.standard_price as standardPrice, cgi.key_num as keyNum, cgi.certification_num as certificationNum, cgi.optional_package_status as optionalPackageStatus, cgi.interest_price as interestPrice, cb.name as brandName, cf.name as familyName, cvc.year, cvc.name as vehicleName, cgi.warehouse_id as warehouseId, cw.name as warehouseName, cgi.storage_id as storageId, cs.name as storageName FROM car_good_info cgi LEFT JOIN car_brand cb on cgi.brand_id = cb.id LEFT JOIN car_family cf on cgi.family_id = cf.id LEFT JOIN car_vehicle_conf cvc on cgi.vehicle_id = cvc.id LEFT JOIN car_warehouse cw on cgi.warehouse_id = cw.id LEFT JOIN car_storage cs on cgi.storage_id = cs.id
and cgi.vin LIKE CONCAT('%', #{vin,jdbcType=VARCHAR} ,'%')
and cgi.family_id = #{familyId,jdbcType=INTEGER}
and cgi.vehicle_id = #{vehicleId,jdbcType=INTEGER}
and cgi.warehouse_id = #{warehouseId,jdbcType=INTEGER}
and cgi.storage_id = #{storageId,jdbcType=INTEGER}
AND cgi.in_storage_day > #{inStorageDay,jdbcType=INTEGER}
and cgi.storage_status = #{storageStatus,jdbcType=INTEGER}
and cgi.dispose_status = #{disposeStatus,jdbcType=INTEGER}
and cgi.order_status = #{orderStatus,jdbcType=INTEGER}
and cgi.storage_status = 2 and cgi.dispose_status = 1 and cgi.order_status = 1
and cgi.storage_status in (1,2,3,5) and cgi.order_status in (1,2) and cgi.dispose_status in (1,2,3,4)
and cgi.storage_status in (2,3,5) and cgi.order_status in (1,2) and cgi.dispose_status in (1,2,3,4)
and cgi.distributor_store_id = #{distributorStoreId,jdbcType=INTEGER} and cgi.forward_car_status = 2 and cgi.status = 1
order by cgi.create_date desc

mapper.update:
update car_good_info
order_status = #{orderStatus,jdbcType=INTEGER}
WHERE id IN ( SELECT car_good_id FROM car_allot WHERE application_id = #{applicationId,jdbcType=INTEGER} )

mapper.select:
SELECT i.id carId, DATE_FORMAT(i.in_storage_date,'%Y-%m-%d %H:%i:%s') storageDate, i.in_storage_day inStorageDay, i.vin, c.`name` vehicleName, c.`year` yearGap, i.decorations_out_name decorationsOutName, i.decorations_in_name decorationsInName, i.engine_num engineNum, i.optional_package_status packgeStatus, i.standard_price standardPrice, i.warehouse_id warehouseId, i.family_id familyId, i.family_name familyName, i.purchase_day purchaseDay, i.fund_property fundProperty, wh.`name` warehouseName, i.storage_id storageId, DATE_FORMAT(i.produce_date,'%Y-%m-%d %H:%i:%s') produceDate, i.key_num keyNum, i.certification_num certificationNum, o.order_num orderNum, e.full_name fullName, o.delivery_date deliveryDate FROM car_good_info i LEFT JOIN car_vehicle_conf c ON i.vehicle_id = c.id LEFT JOIN car_warehouse wh ON i.warehouse_id = wh.id LEFT JOIN `order` o ON o.matching_car_id = i.id LEFT JOIN employee e ON o.create_id = e.id
AND i.storage_status in (1,2,3,5) AND i.distributor_store_id = #{storeId}
AND i.vehicle_id = #{vehicleId}
AND i.vin = #{vin}
AND i.engine_num = #{engineNum}
AND i.decorations_in_id = #{decorationsInId}
AND i.decorations_out_id = #{decorationsOutId}
AND i.year = #{year}
ORDER BY i.in_storage_date

SELECT c.id, c.distributor_store_id, c.family_id, c.vehicle_id, c.brand_id, c.vin, c.engine_num, c.storage_status, c.warehouse_id, c.storage_id, c.decorations_out_id, c.decorations_out_name, c.decorations_in_id, c.decorations_in_name, c.new_boutique_status, c.optional_package_status, c.in_storage_day, c.forward_car_status, cvc.`name` AS chexing_name, cf.`name` AS chexi_name, cb.`name` AS pingpai_name, cw.name cangku_name, cs.name kuwei FROM car_warehouse cw RIGHT JOIN car_storage cs ON cw.id=cs.warehouse_id RIGHT JOIN car_good_info c ON c.storage_id=cs.id LEFT JOIN car_vehicle_conf cvc ON cvc.id=c.vehicle_id LEFT JOIN car_family cf ON cf.id=cvc.family_id LEFT JOIN car_brand cb ON cb.id=cf.brand_id WHERE c.distributor_store_id= cb.distributor_store_id= cf.distributor_store_id= cvc.distributor_store_id
AND c.brand_name LIKE CONCAT('%',#{searchName},'%') OR c.family_name LIKE CONCAT('%',#{searchName},'%') OR c.vehicle_name LIKE CONCAT('%',#{searchName},'%') OR c.decorations_out_name LIKE CONCAT('%',#{searchName},'%') OR c.decorations_in_name LIKE CONCAT('%',#{searchName},'%') OR c.vin LIKE CONCAT('%',#{searchName},'%')
AND decorations_out_name LIKE CONCAT('%',#{outColor},'%')
AND decorations_in_name LIKE CONCAT('%',#{inColor},'%')
AND c.distributor_store_id=#{storeId}
AND c.storage_status=#{storageStatus}
AND c.storage_status=#{storageStatus}
AND c.storage_status IN (3,4,5,6)
AND c.family_id=#{chexiName}
AND c.vehicle_id=#{chexingName}
ORDER BY c.create_date desc

SELECT c.id, c.distributor_store_id, c.family_id, c.vehicle_id, c.brand_id, c.vin, c.engine_num, c.storage_status, c.warehouse_id, c.storage_id, c.decorations_out_id, c.decorations_out_name, c.decorations_in_id, c.decorations_in_name, c.new_boutique_status, c.optional_package_status, c.in_storage_day, cvc.`name` AS chexing_name, cf.`name` AS chexi_name, cb.`name` AS pingpai_name, (SELECT cw.name from car_warehouse cw WHERE cw.id=c.warehouse_id ORDER BY cw.create_date,cw.update_date LIMIT 1) cangku_name, (SELECT name FROM car_storage cs WHERE cs.id=c.storage_id ORDER BY cs.create_date,cs.update_date LIMIT 1) kuwei FROM car_good_info c LEFT JOIN car_vehicle_conf cvc ON cvc.id=c.vehicle_id LEFT JOIN car_family cf ON cf.id=cvc.family_id LEFT JOIN car_brand cb ON cb.id=cf.brand_id WHERE c.distributor_store_id= cb.distributor_store_id= cf.distributor_store_id= cvc.distributor_store_id
AND c.distributor_store_id=#{storeId}
AND c.id=#{id}

SELECT c.id, c.distributor_store_id, c.family_id, c.vehicle_id, c.brand_id, c.vin, c.engine_num, c.storage_status, c.warehouse_id, c.storage_id, c.decorations_out_id, c.decorations_out_name, c.decorations_in_id, c.decorations_in_name, c.new_boutique_status, c.optional_package_status, c.in_storage_day, c.car_check_status, cvc.`name` AS chexing_name, cf.`name` AS chexi_name, cb.`name` AS pingpai_name, (SELECT cw.name from car_warehouse cw WHERE cw.id=c.warehouse_id) cangku_name, (SELECT cs.name FROM car_storage cs WHERE cs.id=c.storage_id) kuwei_name FROM car_good_info c LEFT JOIN car_vehicle_conf cvc ON cvc.id=c.vehicle_id LEFT JOIN car_family cf ON cf.id=cvc.family_id LEFT JOIN car_brand cb ON cb.id=cf.brand_id WHERE c.distributor_store_id= cb.distributor_store_id= cf.distributor_store_id= cvc.distributor_store_id
AND c.distributor_store_id=#{storeId,jdbcType=INTEGER}
AND c.warehouse_id=#{warehouseId,jdbcType=INTEGER}
AND c.car_check_status = #{carCheckStatus,jdbcType=INTEGER} OR c.car_check_status IS NULL
AND c.storage_status = #{storageStatus,jdbcType=INTEGER}

